import React, {useContext} from 'react';
import { StyleSheet, Text, View } from 'react-native';
import {NavigationContainer} from '@react-navigation/native'
import {createStackNavigator} from '@react-navigation/stack'
import {createBottomTabNavigator} from '@react-navigation/bottom-tabs'
import {createDrawerNavigator} from '@react-navigation/drawer'
import { SafeAreaProvider } from 'react-native-safe-area-context';

import SignInScreen from './src/Screens/SignInScreen'
import SignUpScreen from './src/Screens/SignUpScreen'
import TrackCreateScreen from './src/Screens/TrackCreateScreen'
import TrackListScreen from './src/Screens/TrackListScreen'
import TrackDetailScreen from './src/Screens/TrackDetailScreen'
import AccountScreen from './src/Screens/AccountScreen'
import {Provider as AuthProvider, Context as AuthContext} from './src/context/AuthContext'
import {navigationRef} from './RootNavigation'


const LoginFlowStack = createStackNavigator()

const LoginFlow = () => {

  return (
    <LoginFlowStack.Navigator>
      <LoginFlowStack.Screen
        name={'signUp'}
        component={SignUpScreen}
        options={{
          headerShown: false
        }}
      />
      <LoginFlowStack.Screen
        name={'signIn'}
        component={SignInScreen}
        options={{
          headerShown: false
        }}
      />
    </LoginFlowStack.Navigator>
  )
}

const TrackNavigator = createStackNavigator()

const TrackFlow = (props) => {

  const {state} = useContext(AuthContext)
  
  console.log(state)
  

  return (
    <TrackNavigator.Navigator>
      <TrackNavigator.Screen
        name={'trackCreate'}
        component={TrackCreateScreen}
      />
      <TrackNavigator.Screen
        name={'trackDetail'}
        component={TrackDetailScreen}
      />
    </TrackNavigator.Navigator>
  )
}

const MainFlowBottomTab = createBottomTabNavigator()

const MainFlow = (props) => {
  return (
    <MainFlowBottomTab.Navigator>
      <MainFlowBottomTab.Screen
        name={'trackList'}
        component={TrackListScreen}
      />
      <MainFlowBottomTab.Screen
        name={'trackFlow'}
        component={TrackFlow}
      />
      <MainFlowBottomTab.Screen
        name={'account'}
        component={AccountScreen}
      />
    </MainFlowBottomTab.Navigator>
  )
}

const AuthFlow = () => {
  const {state} = useContext(AuthContext)
  const {token, tokenChecked} = state

  return (
    <>
      {tokenChecked ? token !== null ? <MainFlow /> : <LoginFlow /> : false}
    </>
  )
}

const App = () => {
  return (
    <AuthProvider>
      <NavigationContainer ref={navigationRef}>
        <AuthFlow />
      </NavigationContainer>
    </AuthProvider>
  );
}

const styles = StyleSheet.create({
});

export default App
